import { __decorate } from "tslib";
import __NG_CLI_RESOURCE__0 from "./signup.component.html?ngResource";
import __NG_CLI_RESOURCE__1 from "./signup.component.scss?ngResource";
import { ChangeDetectionStrategy, ChangeDetectorRef, Component } from '@angular/core';
import { FormControl, FormGroup, Validators } from '@angular/forms';
import { MAT_RADIO_DEFAULT_OPTIONS } from '@angular/material/radio';
import { MatSnackBar } from '@angular/material/snack-bar';
import { Router } from '@angular/router';
import { TranslateService } from '@ngx-translate/core';
import { finalize } from 'rxjs/operators';
import { SNACK_BAR_DURATION } from '../../../constants/snack-bar-duration.const';
import { FormErrorStateMatcher } from '../../../helpers/error-matchers/form-error-state-matcher.helper';
import { AuthService } from '../../../services/auth/auth.service';
import { SignupService } from '../../../services/auth/signup.service';
import * as AuthCustomValidators from '../../../validators/auth/auth.validators';
let SignupComponent = class SignupComponent {
    constructor(authService, signupService, translateService, changeDetectorRef, snackBar, router) {
        this.authService = authService;
        this.signupService = signupService;
        this.translateService = translateService;
        this.changeDetectorRef = changeDetectorRef;
        this.snackBar = snackBar;
        this.router = router;
        this.formErrorStateMatcher = new FormErrorStateMatcher();
        this.isLoading = false;
        this.form = new FormGroup({
            'language': new FormControl('en', [Validators.required]),
            'weightFormat': new FormControl('kg', [Validators.required]),
            'email': new FormControl(null, [
                Validators.required,
                Validators.email
            ], [AuthCustomValidators.isEmailAvailable(this.signupService, this.changeDetectorRef)]),
            'password': new FormControl(null, [
                Validators.required,
                Validators.minLength(6),
                Validators.maxLength(20)
            ]),
            'confirmPassword': new FormControl(null, [
                Validators.required,
                Validators.minLength(6),
                Validators.maxLength(20)
            ]),
        }, { validators: AuthCustomValidators.samePasswords() });
    }
    onSubmit() {
        if (!this.form.valid) {
            this.snackBar.open(this.translateService.instant('auth.errors.invalid_form'), null, {
                duration: SNACK_BAR_DURATION.ERROR,
                panelClass: 'app__snackbar-error',
            });
            return;
        }
        this.isLoading = true;
        this.authService.signup(this.accessFormData('language').value, this.accessFormData('weightFormat').value, this.accessFormData('email').value, this.accessFormData('password').value, this.accessFormData('confirmPassword').value).pipe(finalize(() => {
            this.isLoading = false;
            this.changeDetectorRef.markForCheck();
        })).subscribe(async (response) => {
            if (response.Success) {
                this.snackBar.open(this.translateService.instant(response.Message), null, {
                    duration: SNACK_BAR_DURATION.GENERAL,
                    panelClass: response.Success ? 'app__snackbar' : 'app__snackbar-error',
                });
                await this.router.navigate(['/auth/login']);
            }
        });
    }
    accessFormData(formFieldName) {
        return this.form.get(formFieldName);
    }
};
